import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);

  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (userData) {
      setUser(JSON.parse(userData));
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <div className="dashboard-container">
      {/* Navbar */}
      <nav className="dashboard-nav">
        <div className="nav-brand">
          <span className="brand-icon">üéµ</span>
          <span className="brand-text">MoodMelody</span>
        </div>
        <div className="nav-links">
          <button onClick={() => navigate('/player')} className="nav-btn">‚ñ∂Ô∏è Player</button>
          <button onClick={() => navigate('/mood-quiz')} className="nav-btn">üòä Mood Quiz</button>
          <button onClick={() => navigate('/journal')} className="nav-btn">üìî Journal</button>
          <button onClick={() => navigate('/profile')} className="nav-btn">üë§ Profile</button>
        </div>
        <button onClick={handleLogout} className="logout-btn">üö™ Logout</button>
      </nav>

      {/* Main Content */}
      <main className="dashboard-main">
        <div className="welcome-section">
          <h1>Welcome back, {user?.name || 'User'}! üéµ</h1>
          <p>How are you feeling today?</p>
        </div>

        <div className="cards-grid">
          <div className="dashboard-card" onClick={() => navigate('/mood-quiz')}>
            <div className="card-icon">üòä</div>
            <h3>Take Mood Quiz</h3>
            <p>Discover music that matches your mood</p>
          </div>

          <div className="dashboard-card" onClick={() => navigate('/player')}>
            <div className="card-icon">üéµ</div>
            <h3>Music Player</h3>
            <p>Listen to your favorite tracks</p>
          </div>

          <div className="dashboard-card" onClick={() => navigate('/journal')}>
            <div className="card-icon">üìî</div>
            <h3>Mood Journal</h3>
            <p>Track your daily emotions</p>
          </div>

          <div className="dashboard-card" onClick={() => navigate('/profile')}>
            <div className="card-icon">üë§</div>
            <h3>Your Profile</h3>
            <p>View and edit your information</p>
          </div>
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
