import React, { useState } from 'react';

export default function Journal({ song, mood, saveJournal }) {
  const [text, setText] = useState('');

  function handleSave() {
    saveJournal({ text, song, mood, date: new Date().toLocaleString() });
    setText('');
  }

  return (
    <div>
      <h2>Your Journal</h2>
      <p>Song: {song} | Mood: {mood}</p>
      <textarea
        value={text}
        onChange={e => setText(e.target.value)}
        placeholder="Write about your experience..."
      />
      <button onClick={handleSave}>Save Journal Entry</button>
    </div>
  );
}
