import React, { useState } from 'react';
import { Music, Eye, EyeOff, Sparkles } from 'lucide-react';

export default function EnhancedLogin() {
  const [isLogin, setIsLogin] = useState(true);
  const [showPassword, setShowPassword] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: ''
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
    // Add your login/signup logic here
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div className="min-h-screen relative overflow-hidden" style={{background: 'linear-gradient(135deg, #F8EDEB 0%, #FCD5CE 100%)'}}>
      {/* Animated background elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute top-20 left-10 w-72 h-72 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse" style={{backgroundColor: '#F9DCC4'}}></div>
        <div className="absolute top-40 right-10 w-72 h-72 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse" style={{backgroundColor: '#A8DADC', animationDelay: '2s'}}></div>
        <div className="absolute -bottom-8 left-1/2 w-72 h-72 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse" style={{backgroundColor: '#457B9D', animationDelay: '4s'}}></div>
      </div>

      {/* Floating musical notes */}
      <div className="absolute inset-0 pointer-events-none">
        {[...Array(8)].map((_, i) => (
          <div
            key={i}
            className="absolute animate-bounce"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animationDelay: `${i * 0.5}s`,
              animationDuration: `${3 + Math.random() * 2}s`
            }}
          >
            <Music style={{color: '#457B9D'}} className="opacity-10" size={20 + Math.random() * 20} />
          </div>
        ))}
      </div>

      {/* Main content */}
      <div className="relative min-h-screen flex items-center justify-center p-4">
        <div className="w-full max-w-md">
          {/* Logo/Brand Section */}
          <div className="text-center mb-8">
            <div className="inline-flex items-center justify-center w-20 h-20 rounded-full mb-4 shadow-lg transform hover:scale-110 transition-transform duration-300" style={{background: 'linear-gradient(135deg, #F9DCC4 0%, #A8DADC 100%)'}}>
              <Music style={{color: '#3D405B'}} size={40} />
            </div>
            <h1 className="text-4xl font-bold mb-2 drop-shadow-lg" style={{color: '#3D405B'}}>
              MoodTunes
            </h1>
            <p style={{color: '#457B9D'}} className="text-sm">Feel the music, express yourself</p>
          </div>

          {/* Login/Signup Card */}
          <div className="backdrop-blur-lg rounded-3xl shadow-2xl p-8 transform hover:scale-105 transition-all duration-300" style={{backgroundColor: 'rgba(255, 255, 255, 0.9)'}}>
            {/* Sparkle decoration */}
            <div className="flex justify-center gap-2 mb-6">
              {[...Array(5)].map((_, i) => (
                <Sparkles
                  key={i}
                  style={{color: '#F9DCC4'}}
                  className="animate-pulse"
                  style={{ animationDelay: `${i * 0.2}s`, color: '#F9DCC4' }}
                  size={16}
                />
              ))}
            </div>

            {/* Title */}
            <h2 className="text-3xl font-bold text-center mb-8" style={{
              background: 'linear-gradient(135deg, #F9DCC4 0%, #457B9D 100%)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              backgroundClip: 'text'
            }}>
              {isLogin ? 'Welcome Back' : 'Join Us'}
            </h2>

            {/* Form Fields */}
            <div className="space-y-5">
              {!isLogin && (
                <div>
                  <label className="block text-sm font-semibold mb-2" style={{color: '#3D405B'}}>
                    Name
                  </label>
                  <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    placeholder="Your name"
                    className="w-full px-4 py-3 rounded-xl border-2 outline-none transition-all duration-300"
                    style={{
                      borderColor: '#A8DADC',
                      backgroundColor: 'white'
                    }}
                    onFocus={(e) => {
                      e.target.style.borderColor = '#457B9D';
                      e.target.style.boxShadow = '0 0 0 4px rgba(69, 123, 157, 0.1)';
                    }}
                    onBlur={(e) => {
                      e.target.style.borderColor = '#A8DADC';
                      e.target.style.boxShadow = 'none';
                    }}
                  />
                </div>
              )}

              <div>
                <label className="block text-sm font-semibold mb-2" style={{color: '#3D405B'}}>
                  Email
                </label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  placeholder="your@email.com"
                  className="w-full px-4 py-3 rounded-xl border-2 outline-none transition-all duration-300"
                  style={{
                    borderColor: '#A8DADC',
                    backgroundColor: 'white'
                  }}
                  onFocus={(e) => {
                    e.target.style.borderColor = '#457B9D';
                    e.target.style.boxShadow = '0 0 0 4px rgba(69, 123, 157, 0.1)';
                  }}
                  onBlur={(e) => {
                    e.target.style.borderColor = '#A8DADC';
                    e.target.style.boxShadow = 'none';
                  }}
                />
              </div>

              <div>
                <label className="block text-sm font-semibold mb-2" style={{color: '#3D405B'}}>
                  Password
                </label>
                <div className="relative">
                  <input
                    type={showPassword ? 'text' : 'password'}
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                    placeholder="Enter password"
                    className="w-full px-4 py-3 rounded-xl border-2 outline-none transition-all duration-300 pr-12"
                    style={{
                      borderColor: '#A8DADC',
                      backgroundColor: 'white'
                    }}
                    onFocus={(e) => {
                      e.target.style.borderColor = '#457B9D';
                      e.target.style.boxShadow = '0 0 0 4px rgba(69, 123, 157, 0.1)';
                    }}
                    onBlur={(e) => {
                      e.target.style.borderColor = '#A8DADC';
                      e.target.style.boxShadow = 'none';
                    }}
                  />
                  <button
                    type="button"
                    onClick={() => setShowPassword(!showPassword)}
                    className="absolute right-4 top-1/2 -translate-y-1/2 transition-colors"
                    style={{color: '#A8DADC'}}
                    onMouseEnter={(e) => e.currentTarget.style.color = '#457B9D'}
                    onMouseLeave={(e) => e.currentTarget.style.color = '#A8DADC'}
                  >
                    {showPassword ? <EyeOff size={20} /> : <Eye size={20} />}
                  </button>
                </div>
              </div>

              {isLogin && (
                <div className="text-right">
                  <button
                    type="button"
                    className="text-sm font-medium transition-colors"
                    style={{color: '#457B9D'}}
                    onMouseEnter={(e) => e.target.style.color = '#3D405B'}
                    onMouseLeave={(e) => e.target.style.color = '#457B9D'}
                  >
                    Forgot password?
                  </button>
                </div>
              )}

              <button
                onClick={handleSubmit}
                className="w-full py-4 rounded-xl font-bold text-lg shadow-lg hover:shadow-xl transform hover:scale-105 active:scale-95 transition-all duration-300 mt-6"
                style={{
                  background: 'linear-gradient(135deg, #F9DCC4 0%, #457B9D 100%)',
                  color: 'white'
                }}
              >
                {isLogin ? 'Login' : 'Sign Up'}
              </button>
            </div>

            {/* Toggle Login/Signup */}
            <div className="text-center mt-6">
              <p className="text-sm" style={{color: '#3D405B'}}>
                {isLogin ? "Don't have an account?" : 'Already have an account?'}
                <button
                  onClick={() => setIsLogin(!isLogin)}
                  className="ml-2 font-bold transition-colors"
                  style={{color: '#F9DCC4'}}
                  onMouseEnter={(e) => e.target.style.color = '#457B9D'}
                  onMouseLeave={(e) => e.target.style.color = '#F9DCC4'}
                >
                  {isLogin ? 'Sign Up' : 'Login'}
                </button>
              </p>
            </div>

            {/* Social Login */}
            <div className="mt-8">
              <div className="relative">
                <div className="absolute inset-0 flex items-center">
                  <div className="w-full border-t" style={{borderColor: '#A8DADC'}}></div>
                </div>
                <div className="relative flex justify-center text-sm">
                  <span className="px-4 bg-white" style={{color: '#457B9D'}}>Or continue with</span>
                </div>
              </div>

              <div className="mt-6">
                <button className="w-full flex items-center justify-center px-4 py-3 rounded-xl border-2 transition-all duration-300 transform hover:scale-105" style={{borderColor: '#A8DADC', backgroundColor: 'white'}}>
                  <svg className="w-5 h-5 mr-2" viewBox="0 0 24 24">
                    <path fill="#4285F4" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
                    <path fill="#34A853" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
                    <path fill="#FBBC05" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
                    <path fill="#EA4335" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
                  </svg>
                  <span className="text-sm font-medium" style={{color: '#3D405B'}}>Continue with Google</span>
                </button>
              </div>
            </div>
          </div>

          {/* Footer text */}
          <p className="text-center mt-6 text-sm drop-shadow" style={{color: '#457B9D'}}>
            Discover music that matches your mood ✨
          </p>
        </div>
      </div>
    </div>
  );
}
