import React, { useState, useEffect } from 'react';
import '../styles/MusicPlaylist.css';

const MusicPlaylist = ({ mood }) => {
  const [tracks, setTracks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchSpotifyPlaylist();
  }, [mood]);

  const fetchSpotifyPlaylist = async () => {
    try {
      setLoading(true);
      setError(null);
      
      console.log('Fetching playlist for mood:', mood);
      const response = await fetch(`http://localhost:5000/api/spotify/playlist/${mood}`);
      
      if (!response.ok) {
        throw new Error('Failed to fetch playlist');
      }
      
      const data = await response.json();
      console.log('Received tracks:', data.tracks);
      setTracks(data.tracks);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching playlist:', error);
      setError(error.message);
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="playlist-container">
        <div className="playlist-card">
          <div className="loading">
            <div className="loading-spinner"></div>
            <h2>üéµ Loading your {mood} playlist...</h2>
            <p>Connecting to Spotify...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="playlist-container">
        <div className="playlist-card">
          <div className="error">
            <h2>üòï Oops! Something went wrong</h2>
            <p>{error}</p>
            <button className="retake-btn" onClick={() => window.location.reload()}>
              üîÑ Try Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="playlist-container">
      <div className="playlist-card">
        <h1 className="playlist-title">üéµ Your {mood} Playlist</h1>
        <p className="playlist-subtitle">
          Powered by Spotify ‚Ä¢ {tracks.length} tracks curated for your mood
        </p>
        
        <div className="songs-list">
          {tracks.map((track) => (
            <div key={track.id} className="song-card">
              <img 
                src={track.image} 
                alt={track.name}
                className="song-image"
              />
              <div className="song-info">
                <div className="song-name">{track.name}</div>
                <div className="song-artist">{track.artist}</div>
              </div>
              <a 
                href={track.spotifyUrl} 
                target="_blank" 
                rel="noopener noreferrer"
                className="play-btn"
              >
                ‚ñ∂Ô∏è Play
              </a>
            </div>
          ))}
        </div>

        <button 
          className="retake-btn"
          onClick={() => window.location.reload()}
        >
          üîÑ Retake Quiz
        </button>
      </div>
    </div>
  );
};

export default MusicPlaylist;
