import React, { useState } from "react";
import '../styles/Login.css';

function Login({ onLogin, goToSignup }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);

  function handleSubmit(e) {
    e.preventDefault();
    onLogin({ email, password });
  }

  return (
    <div className="auth-background">
      <div className="auth-card dreamy-shadow">
        <h2 className="auth-title">Login Now</h2>
        <form onSubmit={handleSubmit}>
          <label>Email</label>
          <input type="email" value={email} onChange={e=>setEmail(e.target.value)} required />
          <label>Password</label>
          <div className="password-row">
            <input
              type={showPassword ? "text" : "password"}
              value={password}
              onChange={e=>setPassword(e.target.value)}
              required
            />
            <span
              className="toggle"
              onClick={() => setShowPassword(s => !s)}
              tabIndex={0}
              title={showPassword ? "Hide" : "Show"}
            >{showPassword ? "üôà" : "üëÅÔ∏è"}</span>
          </div>
          <button className="dreamy-btn" type="submit">Login</button>
        </form>
        <div className="social-row">
          <button className="social-btn facebook-btn">Login with Facebook</button>
          <button className="social-btn google-btn">Login with Google</button>
        </div>
        <div className="auth-action">
          Don't have an account?
          <button className="auth-link" onClick={goToSignup}>Sign Up</button>
        </div>
      </div>
    </div>
  );
}
export default Login;
