import React, { useState } from 'react';
import './styles/App.css';
import MoodQuiz from './components/MoodQuiz';
import Journal from './components/Journal';
import { getRecommendedSong, saveJournal } from './services/apiService';
import Login from './components/Login';
import Signup from './components/Signup';

function App() {
  const [user, setUser] = useState(null);  // NEW: track current login user
  const [moodResult, setMoodResult] = useState(null);
  const [song, setSong] = useState('');
  const [journalEntries, setJournalEntries] = useState([]);

  async function handleMoodSubmit(answers) {
    setMoodResult(answers[0]);
    const recommendedSong = await getRecommendedSong(answers);
    setSong(recommendedSong);
  }

  function handleJournalSave(entry) {
    saveJournal(entry);
    setJournalEntries([...journalEntries, entry]);
  }

  // NEW: Only show Login page if NOT logged in
  if (!user) {
    return (
      <div className="app-container">
        <Login onLogin={setUser} />
      </div>
    );
  }

  // Main app after login
  return (
    <div className="app-container">
      <h1>Mood-Based Song Recommender</h1>
      {!song && <MoodQuiz submitMood={handleMoodSubmit} />}
      {song && (
        <div>
          <h3>Recommended Song: {song}</h3>
          <Journal mood={moodResult} song={song} saveJournal={handleJournalSave} />
        </div>
      )}
      <div>
        <h3>Your Journal Entries:</h3>
        <ul>
          {journalEntries.map((entry, index) => (
            <li key={index}>
              <strong>{entry.date}: </strong>{entry.text}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
