import React, { useState } from 'react';

const questions = [
  { question: "How are you feeling right now?", options: ["Happy", "Sad", "Angry", "Relaxed"] },
  { question: "Would you prefer energetic or calm music?", options: ["Energetic", "Calm"] },
];

export default function MoodQuiz({ submitMood }) {
  const [answers, setAnswers] = useState([]);
  const [current, setCurrent] = useState(0);

  function handleAnswer(option) {
    const updated = [...answers, option];
    setAnswers(updated);
    if (current < questions.length - 1) {
      setCurrent(current + 1);
    } else {
      submitMood(updated);
    }
  }

  return (
    <div>
      <h2>Mood Quiz</h2>
      <p>{questions[current].question}</p>
      {questions[current].options.map(option => (
        <button key={option} onClick={() => handleAnswer(option)}>
          {option}
        </button>
      ))}
    </div>
  );
}
