import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/Auth.css';

const Login = () => {
  const navigate = useNavigate();
  const [isLogin, setIsLogin] = useState(true);
  const [showPassword, setShowPassword] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: ''
  });
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [apiError, setApiError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
    setApiError('');
  };

  const validateForm = () => {
    const newErrors = {};

    if (!isLogin && !formData.name) {
      newErrors.name = 'Name is required';
    }

    if (!formData.email) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!validateForm()) {
      return;
    }

    setLoading(true);
    setApiError('');

    try {
      const endpoint = isLogin ? '/api/auth/login' : '/api/auth/signup';
      const response = await axios.post(
        `${process.env.REACT_APP_API_URL}${endpoint}`,
        formData
      );

      localStorage.setItem('token', response.data.token);
      localStorage.setItem('user', JSON.stringify(response.data.user));

      navigate('/dashboard');
    } catch (error) {
      console.error('Auth error:', error);
      setApiError(
        error.response?.data?.message || 
        `${isLogin ? 'Login' : 'Signup'} failed. Please try again.`
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-page">
      {/* Animated background blobs */}
      <div className="animated-bg">
        <div className="blob blob-1"></div>
        <div className="blob blob-2"></div>
        <div className="blob blob-3"></div>
      </div>

      {/* Floating musical notes */}
      <div className="floating-notes">
        {[...Array(8)].map((_, i) => (
          <div
            key={i}
            className="floating-note"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animationDelay: `${i * 0.5}s`,
              animationDuration: `${3 + Math.random() * 2}s`
            }}
          >
            üéµ
          </div>
        ))}
      </div>

      {/* Main content */}
      <div className="auth-container">
        <div className="auth-content">
          {/* Logo Section */}
          <div className="auth-logo-section">
            <div className="auth-logo">
              <span className="music-icon">üéµ</span>
            </div>
            <h1 className="auth-brand">Mood Melody</h1>
            <p className="auth-tagline">Feel the music, express yourself</p>
          </div>

          {/* Login/Signup Card */}
          <div className="auth-card-enhanced">
            {/* Sparkles decoration */}
            <div className="sparkles-row">
              {[...Array(5)].map((_, i) => (
                <span
                  key={i}
                  className="sparkle"
                  style={{ animationDelay: `${i * 0.2}s` }}
                >
                  ‚ú®
                </span>
              ))}
            </div>

            {/* Title */}
            <h2 className="auth-title-gradient">
              {isLogin ? 'Welcome Back' : 'Join Us'}
            </h2>

            {/* Error Banner */}
            {apiError && (
              <div className="error-banner">
                {apiError}
              </div>
            )}

            {/* Form */}
            <form onSubmit={handleSubmit} className="auth-form-enhanced">
              {!isLogin && (
                <div className="form-group slide-in">
                  <label htmlFor="name">Name</label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    placeholder="Your name"
                    className={errors.name ? 'input-error' : ''}
                  />
                  {errors.name && (
                    <span className="error-text">{errors.name}</span>
                  )}
                </div>
              )}

              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  placeholder="your@email.com"
                  className={errors.email ? 'input-error' : ''}
                />
                {errors.email && (
                  <span className="error-text">{errors.email}</span>
                )}
              </div>

              <div className="form-group">
                <label htmlFor="password">Password</label>
                <div className="password-input-wrapper">
                  <input
                    type={showPassword ? 'text' : 'password'}
                    id="password"
                    name="password"
                    value={formData.password}
                    onChange={handleChange}
                    placeholder="Enter password"
                    className={errors.password ? 'input-error' : ''}
                  />
                  <button
                    type="button"
                    className="password-toggle-btn"
                    onClick={() => setShowPassword(!showPassword)}
                    aria-label={showPassword ? 'Hide password' : 'Show password'}
                  >
                    {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
                  </button>
                </div>
                {errors.password && (
                  <span className="error-text">{errors.password}</span>
                )}
              </div>

              {isLogin && (
                <div className="forgot-password-row">
                  <button
                    type="button"
                    className="forgot-password-link"
                  >
                    Forgot password?
                  </button>
                </div>
              )}

              <button
                type="submit"
                className="auth-button-gradient"
                disabled={loading}
              >
                {loading ? (isLogin ? 'LOGGING IN...' : 'SIGNING UP...') : (isLogin ? 'LOGIN' : 'SIGN UP')}
              </button>
            </form>

            {/* Toggle Login/Signup */}
            <div className="auth-toggle">
              <p>
                {isLogin ? "Don't have an account?" : 'Already have an account?'}
                <button
                  type="button"
                  onClick={() => {
                    setIsLogin(!isLogin);
                    setErrors({});
                    setApiError('');
                  }}
                  className="auth-toggle-link"
                >
                  {isLogin ? 'Sign Up' : 'Login'}
                </button>
              </p>
            </div>

            {/* Social Login - Google only */}
            <div className="social-login">
              <div className="divider">
                <span>Or continue with</span>
              </div>

              <button type="button" className="social-btn google-btn">
                <svg className="social-icon" viewBox="0 0 24 24">
                  <path fill="#4285F4" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
