import React, { useState } from "react";
import "./Signup.css";

function Signup({ onSignup, goToLogin }) {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);

  function handleSubmit(e) {
    e.preventDefault();
    // Use your actual signup logic here
    onSignup({ username, email, password });
  }

  return (
    <div className="auth-background">
      <div className="auth-card dreamy-shadow">
        <h2 className="auth-title">Sign Up Now</h2>
        <form onSubmit={handleSubmit}>
          <label>Username</label>
          <input type="text" value={username} onChange={e=>setUsername(e.target.value)} required />

          <label>Email</label>
          <input type="email" value={email} onChange={e=>setEmail(e.target.value)} required />

          <label>Password</label>
          <div className="password-row">
            <input
              type={showPassword ? "text" : "password"}
              value={password}
              onChange={e=>setPassword(e.target.value)}
              required
            />
            <span className="toggle" onClick={() => setShowPassword(s => !s)}>{showPassword ? "üôà" : "üëÅÔ∏è"}</span>
          </div>
          <button className="dreamy-btn" type="submit">Sign Up</button>
        </form>
        <div className="social-row">
          <button className="social-btn facebook-btn">Login with Facebook</button>
          <button className="social-btn google-btn">Login with Google</button>
        </div>
        <div className="auth-action">
          Already have an account?
          <button className="auth-link" onClick={goToLogin}>Login</button>
        </div>
      </div>
    </div>
  );
}
export default Signup;
