import axios from 'axios';

export async function getRecommendedSong(moodAnswers) {
  const response = await axios.post('http://localhost:5000/api/recommend', { moodAnswers });
  return response.data.song;
}

export async function saveJournal(journalEntry) {
  await axios.post('http://localhost:5000/api/journal', journalEntry);
}
